import React, {Component} from 'react';
import SimpleReactCalendar from 'simple-react-calendar'
import './Myhabits.css'
let habits = [
    {
        id:1,
        habit:"Work Out",
        duration:30,
        commitment:5,
        startDate: '2019-4-19',
        endDate: '2019-4-20'
    }
]

export class MyHabits extends Component {
    constructor(){
        super()
        this.state = {
            habits:habits
        }
    }
    render(){
        return(
            <div>
                <ul>
                    {this.state.habits.map(row => {
                        return (
                            <li>
                                <h1>{row.habit}</h1>
                                <h4>Duration: {row.duration} days</h4>
                                <h4>How Much You've Committed to the Cause: {row.commitment} Dollars</h4>
                                <h5>Start Date: {row.startDate}</h5>
                                <h5>End Date: {row.endDate}</h5>
                                <SimpleReactCalendar weekStartsOn = {0} highlightedStart = {{start: this.state.startDate, end: this.state.endDate}}/>
                                <h5>Total Time: {6} Days</h5>
                            </li>
                        )
                    })}
                </ul>
            </div>
        )
    }
}
