import React, {Component} from 'react';
import Calendar from 'simple-react-calendar'
import './Myhabits.css'


let habits = [
    {
        id:1,
        habit:"Work Out",
        duration:30,
        commitment:5,
        startDate: '2019-4-19',
        endDate: '2019-8-29'
    }
]


export class MyHabits extends Component {
    constructor(){
        super()
        this.state = {
            habits:habits
        }
    }
    render(){
        let calendarRender = this.state.habits.map(row => {
            return (
                <li>
                    <h1>{row.habit}</h1>
                    <h4>Duration: {row.duration} days</h4>
                    <h4>How Much You've Committed to the Cause: {row.commitment} Dollars</h4>
                    <h5>Start Date: {row.startDate}</h5>
                    <h5>End Date: {row.endDate}</h5>
                    <div className = 'calendars'>
                      <Calendar maxDate = {'2000-01-01'} activeMonth = {new Date(row.startDate)} mode = {'range'} selected = {{start: new Date(row.startDate), end: new Date(row.startDate)}} weekStartsOn = {0} />
                      <Calendar maxDate = {'2000-01-01'} activeMonth = {new Date(row.endDate)} mode = {'range'} selected = {{start: new Date(row.endDate), end: new Date(row.endDate)}} weekStartsOn = {0} />
                    </div>
                    <h5>Total Time: {(new Date(row.endDate) - new Date(row.startDate))/(1000*60*60*24) + 1} Days</h5>
                </li>
            )
        })



        return(
            <div>
                <ul className = 'habits'>
                {calendarRender}
                </ul>
            </div>
        )
    }
}
